import './App.css';
// import Suma from './funcion';

const Header =(props)=>{
  return <h1>{props.nombre} </h1>
}

const Content = (props) => {
  console.log(props)
  return (
    <div>
      <p>{props.parts[0].name} {props.parts[0].exercises}</p>
      <p>{props.parts[1].name} {props.parts[1].exercises}</p>
      <p>{props.parts[2].name} {props.parts[2].exercises}</p>
    </div>
  );
};

